  def revoke_o_auth2_login_sessions(connection, opts \\ []) do
    optional_params = %{
      :subject => :query,
      :sid => :query
    }

    request =
      %{}
      |> method(:delete)
      |> url("/admin/oauth2/auth/sessions/login")
      |> add_optional_params(optional_params, opts)
      |> Enum.into([])

    connection
    |> Connection.request(request)
    |> evaluate_response([
      {204, false},
      {:default, Ory.Model.ErrorOAuth2}
    ])
  end

  @doc """
  Revoke OAuth 2.0 Access or Refresh Token
  Revoking a token (both access and refresh) means that the tokens will be invalid. A revoked access token can no longer be used to make access requests, and a revoked refresh token can no longer be used to refresh an access token. Revoking a refresh token also invalidates the access token that was created with it. A token may only be revoked by the client the token was generated for.

  ### Parameters

  - `connection` (Ory.Connection): Connection to server
  - `token` (String.t): 
  - `opts` (keyword): Optional parameters
    - `:client_id` (String.t): 
    - `:client_secret` (String.t): 

  ### Returns

  - `{:ok, nil}` on success
  - `{:error, Tesla.Env.t}` on failure
  """
  @spec revoke_o_auth2_token(Tesla.Env.client, String.t, keyword()) :: {:ok, nil} | {:ok, Ory.Model.ErrorOAuth2.t} | {:error, Tesla.Env.t}
  def revoke_o_auth2_token(connection, token, opts \\ []) do
    optional_params = %{
      :client_id => :form,
      :client_secret => :form
    }

    request =
      %{}
      |> method(:post)
      |> url("/oauth2/revoke")
      |> add_param(:form, :token, token)
      |> add_optional_params(optional_params, opts)
      |> Enum.into([])

    connection
    |> Connection.request(request)
    |> evaluate_response([
      {200, false},
      {:default, Ory.Model.ErrorOAuth2}
    ])
  end

