      :Cookie => :headers
    }
    %{}
    |> method(:get)
    |> url("/sessions/whoami")
    |> add_optional_params(optional_params, opts)
    |> Enum.into([])
    |> (&Connection.request(connection, &1)).()
    |> evaluate_response([
      { 200, %Ory.Model.Session{}},
      { 401, %Ory.Model.JsonError{}},
      { 403, %Ory.Model.JsonError{}},
